import{_ as p,X as e,Y as t,a3 as a}from"./framework-e3372978.js";const c={},n=a('<h1 id="冬之语【十】" tabindex="-1"><a class="header-anchor" href="#冬之语【十】" aria-hidden="true">#</a> 冬之语【十】</h1><div style="text-align:center;"><p>深秋萧瑟晚冬更凉，</p><p>瘦枝浅草不堪寒霜。</p><p>枫林卸红妆，</p><p>蒹葭覆苍苍，</p><p>残朵落英满地殇。</p><p>♢</p><p>依稀旧榭笛声飞扬，</p><p>何觅白衣子见疏狂?</p><p>眉锁景一方，</p><p>眸拆泪两行，</p><p>千结心似双丝网。</p><p>♢</p><p>陌上似曾牵手醉春光?</p><p>花下几许寒衣徒自香?</p><p>冷夜倚梦长，</p><p>冰晨空怅惘，</p><p>山水迢迢两茫茫。</p><p>♢</p><p>旧时光景不忍回头望，</p><p>何日与子重逢不敢想。</p><p>花语指尖芳，</p><p>心事弦上淌，</p><p>万般愁绪费思量。</p><p>♢</p><p>当梅雪飘香，</p><p>北风无觅梦里荒唐，</p><p>卷帘影成双；</p><p>当候鸟鸣唱，</p><p>煮酒高歌迷离远方，</p><p>我为你守望。</p></div>',2),r=[n];function s(_,o){return e(),t("div",null,r)}const d=p(c,[["render",s],["__file","10.html.vue"]]);export{d as default};
