import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as t,b as a}from"./app-fd69f1a1.js";const c={},r=a('<h1 id="秋之思【五】" tabindex="-1"><a class="header-anchor" href="#秋之思【五】" aria-hidden="true">#</a> 秋之思【五】</h1><div style="text-align:center;"><p>浅秋，静守时间的光年。</p><p>我在季节的暖光里，</p><p>追逐那蝶翼的翕动，</p><p>捻一瓣细微的花语，</p><p>颤醒了一萝青藤的呢喃。</p><p>依着情绪的彩铅，</p><p>在心事上抹下了</p><p>紫色的薰衣草，</p><p>火红的枫叶，</p><p>金黄的麦田，</p><p>含着馨香宁好，</p><p>于安逸岁月里面，</p><p>留下了静默无言。</p><p>♢</p><p>阳光，踮起脚尖，</p><p>轻盈的亲吻眼睑，</p><p>清幽了风的暖意，</p><p>吹散了雨丝连绵。</p><p>于是我便把秋的诗篇</p><p>叙述在这份安宁间。</p><p>那细微的欢喜，</p><p>你可听见?</p></div>',2),o=[r];function n(s,_){return e(),t("div",null,o)}const l=p(c,[["render",n],["__file","5.html.vue"]]);export{l as default};
