import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as t,f as a}from"./app-f910c21e.js";const c={},r=a('<h1 id="秋之思【八】" tabindex="-1"><a class="header-anchor" href="#秋之思【八】" aria-hidden="true">#</a> 秋之思【八】</h1><div style="text-align:center;"><p>窗外，</p><p>秋风瑟瑟、潇雨淋漓;</p><p>心中，</p><p>草木葳蕤、花开淡雅;</p><p>美文，</p><p>如同走在平仄韵律的诗行，</p><p>让人生在这馨暖中静静生香。</p><p>虽然这些青涩的墨色诗篇，</p><p>拢不起时光中的句句点点，</p><p>但我要怀着</p><p>一丝香息的思念，</p><p>一份草木的情怀，</p><p>在薄薄的岁月里，</p><p>让那些细碎清痕，</p><p>在生活的点滴里，</p><p>盈一份悠然诗意。</p><p>再把清秋的容颜</p><p>于光阴中清喜展现。</p><p>希望它还是</p><p>一样的天高云淡，</p><p>一样的朴实安素，</p><p>一样的岁月嫣然。</p></div>',2),o=[r];function n(s,_){return e(),t("div",null,o)}const l=p(c,[["render",n],["__file","8.html.vue"]]);export{l as default};
